    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        Finalizar fin = new Finalizar();
        String cmbtemp = (String)cmb_temperatura.getSelectedItem();
        
        switch (cmbtemp) {
            case "celius a kelvin" :
                JOptionPane.showMessageDialog(this,"Celcius a Kelvin: "+Temperatura.celiuskelvin());
                fin.setVisible(true);
                this.dispose();
                break;
            case "celcius a farenhgeith" :
                JOptionPane.showMessageDialog(this,"celcius a farenhgeith"+Temperatura.celciusfarenhgeith());
                fin.setVisible(true);
                this.dispose();
                break;
            case "kelvin a celcius" :
                JOptionPane.showMessageDialog(this,"kelvin a celcius"+Temperatura.kelvincelcius());
                fin.setVisible(true);
                this.dispose();
                break;
            case "kelvin a farenhgeith" :
                JOptionPane.showMessageDialog(this,"kelvin a farenhgeith"+Temperatura.kelvinfarenhgeith());
                fin.setVisible(true);
                this.dispose();
                break;
            case "farenhgeith a celcius" :
                JOptionPane.showMessageDialog(this,"farenhgeith a celcius"+Temperatura.farenhgeithcelcius());
                fin.setVisible(true);
                this.dispose();
                break;
            case "farenhgeith a kelvin" :
                JOptionPane.showMessageDialog(this,"farenhgeith a kelvin"+Temperatura.farenhgeithkelvin());
                fin.setVisible(true);
                this.dispose();
                break;
            default :
                throw new AssertionError();
        }
    }//GEN-LAST:event_jButton1ActionPerformed

